package basics.corejava;

public class Variables {

	// CLASS VARIABLES OR INSTANCE VARIABLES

	int a = 50;

	// STATIC VARIABLES

	static float f = 12.8f;

	// 	LOCAL VARIABLES

	public void test() {
		int i = 797;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int j = 560;

		System.out.println("local variables is " + j);

	}

}
